import React, { useState } from "react";
import { Grid } from "@material-ui/core";
import favourite from "../../data/favoutire.json";
import { sortBy, useToggle, useInput, useTitle } from "../../utils/General";
import List from "../../components/List/List";
import Movie from "../../components/Movie/Movie";
import Setting from "../../components/Setting/Setting";

const moduleLayout = {
  xs: 12,
  sm: 6,
  md: 4,
  lg: 3,
  xl: 3,
};

const listLayout = {
  xs: 12,
  sm: 12,
  md: 12,
  lg: 6,
  xl: 6,
};

const Main = () => {
  const [sortValue, setLSortValue] = useState("");
  const { toggle, setToggle } = useToggle();
  const [searchValue, setSearchValue] = useInput();
  useTitle("Filmes | Sthima");

  React.useEffect(() => {
    (async () => setArrayPaises(await SearchData("/maatdigital/paises")))();
  }, []);

  const props = {
    toggle,
    setToggle,
    searchValue,
    setSearchValue,
    setLSortValue,
  };

  const layout = toggle ? listLayout : moduleLayout;
  const documentaries = sortBy(
    favourite.filter(({ docTitle }) =>
      docTitle.toLowerCase().includes(searchValue.toLowerCase())
    ),
    (a, b) => b[sortValue] - a[sortValue]
  );

  return (
    <>
      <Setting {...props} />
      <Grid container spacing={4}>
        {documentaries.map((documentary, key) => (
          <Grid item key={key} {...layout}>
            {toggle ? <List {...documentary} /> : <Movie {...documentary} />}
          </Grid>
        ))}
      </Grid>
    </>
  );
};

export default Main;
