import React from "react";
import { Toolbar, AppBar, TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/styles";
import Autocomplete from "@material-ui/lab/Autocomplete";
import SearchData from "../../functions/SearchData/SearchData";

const useStyles = makeStyles((theme) => ({
  appBar: {
    backgroundColor: theme.palette.type === "dark" ? "#1e1e1e" : "#ffffff",
    opacity: 0.85,
  },
  option: {
    fontSize: 15,
    "& > span": {
      marginRight: 10,
      fontSize: 18,
    },
  },
}));

function AppBarChild() {
  const classes = useStyles();
  const [arrayMovieA2021, setArrayMovieA2021] = React.useState([]);
  const [nameMovie, setNameMovie] = React.useState("");

  React.useEffect(() => {
    (async () =>
      setArrayMovieA2021(
        await SearchData(!nameMovie ? "batman" : nameMovie)
      ))();
  }, [nameMovie]);

  const renderMovieCapa = (movieCapa) => (
    <img src={movieCapa} alt="Imagem" width="50" />
  );

  return (
    <AppBar position="fixed" className={classes.appBar}>
      <Toolbar disableGutters variant="regular">
        <Autocomplete
          fullWidth
          classes={{
            option: classes.option,
          }}
          onChange={(e, v) => setNameMovie(!v ? "" : v.Title)}
          options={arrayMovieA2021}
          getOptionLabel={(option) => option.Title}
          renderOption={(option) => (
            <React.Fragment>
              <span>{renderMovieCapa(option.Poster)}</span>
              {option.Title}
            </React.Fragment>
          )}
          renderInput={(params) => (
            <TextField
              {...params}
              aria-labelledby="Movies"
              id="movies"
              type="text"
              label="Escolha um Filme"
              variant="outlined"
              margin="dense"
              fullWidth
            />
          )}
        />
      </Toolbar>
    </AppBar>
  );
}

export default AppBarChild;
