import React from "react";
import { IconButton, Toolbar, AppBar, TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/styles";
import { InsertLink } from "@material-ui/icons";
import Autocomplete from "@material-ui/lab/Autocomplete";
import SearchData from "../../functions/SearchData/SearchData";

const useStyles = makeStyles((theme) => ({
  appBar: {
    backgroundColor: theme.palette.type === "dark" ? "#1e1e1e" : "#ffffff",
    opacity: 0.85,
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  grow: {
    flexGrow: 1,
  },
  cardLink: {
    textDecoration: "none",
    color: theme.palette.type === "dark" ? "#fbfbfb" : "#1e1e1e",
  },
}));

function AppBarChild() {
  const classes = useStyles();
  const [arrayMovieA2021, setArrayMovieA2021] = React.useState([]);
  const [nameMovie, setNameMovie] = React.useState("");

  React.useEffect(() => {
    (async () =>
      setArrayMovieA2021(
        await SearchData(!nameMovie ? "batman" : nameMovie)
      ))();
  }, []);

  return (
    <AppBar position="absolute" className={classes.appBar}>
      <Toolbar disableGutters variant="dense">
        <div className={classes.grow} />
        <Autocomplete
          onChange={(e, v) => setNameMovie(!v ? "batman" : v.Title)}
          options={arrayMovieA2021}
          renderInput={(params) => (
            <TextField
              {...params}
              aria-labelledby="Movies"
              id="movies"
              type="text"
              label="Filmes"
              helperText="Escolha um Filme"
              variant="outlined"
              margin="dense"
              fullWidth
            />
          )}
        />
        <IconButton aria-label="Site">
          <a
            target="_blank"
            aria-label="Site"
            rel="noopener noreferrer"
            href={"https://sthima.com.br/"}
            className={classes.cardLink}
          >
            <InsertLink fontSize="small" />
          </a>
        </IconButton>
      </Toolbar>
    </AppBar>
  );
}

export default AppBarChild;
